import { useLanguage } from '@/hooks/useLanguage';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Droplets, Heart, Sword } from 'lucide-react';
import type { Card } from '@shared/schema';

interface CardModalProps {
  card: Card;
  isOpen: boolean;
  onClose: () => void;
}

export function CardModal({ card, isOpen, onClose }: CardModalProps) {
  const { t, language } = useLanguage();

  const cardName = language === 'pt-BR' ? card.name : card.nameEn;
  const cardDescription = language === 'pt-BR' ? card.description : card.descriptionEn;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="bg-game-card border-game-muted max-w-md">
        <DialogHeader>
          <DialogTitle className="text-center text-2xl font-bold text-white">
            {cardName}
          </DialogTitle>
        </DialogHeader>

        <div className="text-center space-y-6">
          {card.imageUrl && (
            <img
              src={card.imageUrl}
              alt={cardName}
              className="w-48 h-60 object-cover rounded-xl mx-auto shadow-lg"
            />
          )}

          <div className="space-y-3">
            <div className="flex items-center justify-center space-x-4">
              <div className="bg-game-orange bg-opacity-20 px-4 py-2 rounded-lg">
                <span className="text-game-orange font-bold text-xl">{card.elixirCost}</span>
                <Droplets className="inline text-purple-400 ml-2 h-5 w-5" />
              </div>
              <div className="bg-game-muted bg-opacity-50 px-4 py-2 rounded-lg">
                <span className="text-game-text">{card.rarity}</span>
              </div>
            </div>

            {cardDescription && (
              <p className="text-game-text text-sm px-4">{cardDescription}</p>
            )}
          </div>

          {(card.hitpoints || card.damage) && (
            <div className="grid grid-cols-2 gap-4 text-center">
              {card.hitpoints && (
                <div className="bg-game-muted bg-opacity-30 p-3 rounded-lg">
                  <div className="flex items-center justify-center mb-1">
                    <Heart className="text-red-400 h-4 w-4 mr-1" />
                    <div className="text-white font-semibold">{card.hitpoints}</div>
                  </div>
                  <div className="text-sm text-game-text">{language === 'pt-BR' ? 'Vida' : 'Hitpoints'}</div>
                </div>
              )}
              {card.damage && (
                <div className="bg-game-muted bg-opacity-30 p-3 rounded-lg">
                  <div className="flex items-center justify-center mb-1">
                    <Sword className="text-orange-400 h-4 w-4 mr-1" />
                    <div className="text-white font-semibold">{card.damage}</div>
                  </div>
                  <div className="text-sm text-game-text">{language === 'pt-BR' ? 'Dano' : 'Damage'}</div>
                </div>
              )}
            </div>
          )}

          <Button
            onClick={onClose}
            className="w-full py-3 bg-game-muted text-white rounded-lg hover:bg-game-orange transition-colors"
            data-testid="button-close-modal"
          >
            {t('common.close')}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Header.tsx.content:
import { useState } from 'react';
import { Link, useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { useAuth } from '@/hooks/useAuth';
import { useLanguage } from '@/hooks/useLanguage';
import { LanguageToggle } from './LanguageToggle';
import { Crown, Menu, X } from 'lucide-react';

export default function Header() {
  const [, setLocation] = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const { isAuthenticated, user } = useAuth();
  const { t } = useLanguage();

  const navigation = [
    { key: 'home', href: '/', label: t('header.home') },
    { key: 'training', href: '/training', label: t('header.training') },
    { key: 'cards', href: '/cards', label: t('header.cards') },
    { key: 'clans', href: '/clans', label: t('header.clans') },
    { key: 'tournaments', href: '/tournaments', label: t('header.tournaments') },
  ];

  const handleLogin = () => {
    window.location.href = '/api/login';
  };

  const handleLogout = () => {
    window.location.href = '/api/logout';
  };

  return (
    <header className="bg-game-card border-b border-game-muted sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo and Brand */}
          <div className="flex items-center space-x-4">
            <Link href="/" className="flex items-center space-x-2">
              <Crown className="text-game-orange text-2xl" />
              <h1 className="text-xl font-bold text-white">CR Trainer</h1>
            </Link>
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden md:flex items-center space-x-8">
            {navigation.map((item) => (
              <Link
                key={item.key}
                href={item.href}
                className="text-game-text hover:text-game-orange transition-colors"
                data-testid={`nav-${item.key}`}
              >
                {item.label}
              </Link>
            ))}
            {isAuthenticated && user?.permission === 10 && (
              <Link
                href="/admin"
                className="text-game-text hover:text-game-orange transition-colors"
                data-testid="nav-admin"
              >
                Admin
              </Link>
            )}
          </nav>

          {/* Language Toggle and Auth */}
          <div className="flex items-center space-x-4">
            <LanguageToggle />

            {/* Auth Buttons */}
            <div className="hidden md:flex items-center space-x-2">
              {isAuthenticated ? (
                <div className="flex items-center space-x-2">
                  {user?.profileImageUrl && (
                    <img
                      src={user.profileImageUrl}
                      alt="Profile"
                      className="w-8 h-8 rounded-full object-cover"
                    />
                  )}
                  <Button
                    onClick={handleLogout}
                    variant="outline"
                    size="sm"
                    data-testid="button-logout"
                  >
                    {t('header.logout')}
                  </Button>
                </div>
              ) : (
                <Button
                  onClick={handleLogin}
                  size="sm"
                  className="bg-game-orange hover:bg-game-orange/90"
                  data-testid="button-login"
                >
                  {t('header.login')}
                </Button>
              )}
            </div>

            {/* Mobile Menu Button */}
            <button
              className="md:hidden text-game-text hover:text-white"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              data-testid="button-mobile-menu"
            >
              {isMobileMenuOpen ? <X className="text-xl" /> : <Menu className="text-xl" />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <div className="md:hidden bg-game-card border-t border-game-muted">
          <div className="px-4 py-4 space-y-4">
            {navigation.map((item) => (
              <Link
                key={item.key}
                href={item.href}
