    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/cards'] });
      setIsCardModalOpen(false);
      setCardForm({
        name: '',
        nameEn: '',
        elixirCost: 1,
        rarity: 'Common',
        type: 'Troop',
        description: '',
        descriptionEn: '',
        hitpoints: undefined,
        damage: undefined,
        imageUrl: '',
      });
      toast({
        title: t('common.success'),
        description: 'Carta criada com sucesso!',
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: t('common.error'),
        description: 'Erro ao criar carta',
        variant: 'destructive',
      });
    },
  });

  const updateCardMutation = useMutation({
    mutationFn: async ({ id, card }: { id: string; card: Partial<InsertCard> }) => {
      await apiRequest('PUT', `/api/cards/${id}`, card);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/cards'] });
      setIsCardModalOpen(false);
      setEditingCard(null);
      toast({
        title: t('common.success'),
        description: 'Carta atualizada com sucesso!',
      });
    },
