import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { useLanguage } from '@/hooks/useLanguage';
import { useAuth } from '@/hooks/useAuth';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import Header from '@/components/Header';
import { Search, Trophy, Users, Clock, Crown } from 'lucide-react';

interface Tournament {
  tag: string;
  name: string;
  description: string;
  status: string;
  createdTime: string;
  startedTime?: string;
  endedTime?: string;
  capacity: number;
  maxCapacity: number;
  preparationDuration: number;
  duration: number;
  firstPlaceCardPrize: number;
  type: string;
  creatorTag: string;
  levelCap: number;
  gameMode?: {
    name: string;
    id: number;
  };
}

export default function Tournaments() {
  const { t } = useLanguage();
  const { isAuthenticated } = useAuth();
  const [searchName, setSearchName] = useState('');
  const [hasSearched, setHasSearched] = useState(false);

  const { data: tournaments = [], isLoading, error } = useQuery<Tournament[]>({
    queryKey: ['/api/clash-royale/tournaments', { name: searchName }],
    enabled: hasSearched && searchName.length >= 3,
    retry: false,
  });

  const handleSearch = () => {
    if (searchName.length < 3) {
      return;
    }
    setHasSearched(true);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'inProgress': return 'bg-green-500 bg-opacity-20 text-green-400';
      case 'preparing': return 'bg-yellow-500 bg-opacity-20 text-yellow-400';
      case 'ended': return 'bg-red-500 bg-opacity-20 text-red-400';
      case 'full': return 'bg-orange-500 bg-opacity-20 text-orange-400';
      default: return 'bg-game-muted bg-opacity-30 text-game-text';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'inProgress': return 'Em Andamento';
      case 'preparing': return 'Preparando';
      case 'ended': return 'Finalizado';
      case 'full': return 'Lotado';
      default: return status;
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (!isAuthenticated) {
    return (
      <div className="bg-game-dark text-game-text font-game min-h-screen">
        <Header />
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="text-center">
            <h1 className="text-2xl font-bold text-white mb-4">Login Required</h1>
            <p className="text-game-text mb-6">You need to be logged in to access tournament data.</p>
            <Button
              onClick={() => window.location.href = '/api/login'}
              className="bg-game-orange hover:bg-game-orange/90"
              data-testid="button-login-tournaments"
            >
              Login to Continue
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-game-dark text-game-text font-game min-h-screen">
      <Header />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-3xl lg:text-4xl font-bold text-white mb-4">
            <Trophy className="inline mr-2 text-green-400" />
            Torneios
          </h1>
          <p className="text-xl text-game-text">Encontre torneios ativos e acompanhe competiÃ§Ãµes em tempo real</p>
        </div>

        {/* Search Form */}
        <Card className="bg-game-card border-game-muted mb-8">
          <CardHeader>
            <CardTitle className="text-white flex items-center">
              <Search className="mr-2 text-game-orange" />
              Buscar Torneios
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex gap-4">
              <div className="flex-1">
                <Label htmlFor="tournamentName" className="text-game-text">Nome do Torneio</Label>
                <Input
                  id="tournamentName"
                  placeholder="Digite pelo menos 3 caracteres"
                  value={searchName}
                  onChange={(e) => setSearchName(e.target.value)}
                  className="bg-game-muted border-game-muted text-white"
                  data-testid="input-tournament-name"
                />
              </div>

              <div className="flex items-end">
                <Button
                  onClick={handleSearch}
                  className="bg-game-orange hover:bg-game-orange/90"
                  disabled={searchName.length < 3 || isLoading}
                  data-testid="button-search-tournaments"
                >
                  <Search className="mr-2 h-4 w-4" />
                  Buscar
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Search Results */}
        {error && (
          <Card className="bg-game-card border-red-500 mb-8">
            <CardContent className="pt-6">
              <div className="text-center text-red-400">
                <p className="mb-2">Erro ao buscar torneios</p>
                <p className="text-sm text-game-text">
                  {error instanceof Error ? error.message : 'Verifique sua conexÃ£o e tente novamente'}
                </p>
              </div>
            </CardContent>
          </Card>
        )}

        {isLoading && (
          <div className="text-center py-12">
            <p className="text-game-text">{t('common.loading')}</p>
          </div>
        )}

        {hasSearched && !isLoading && !error && tournaments.length === 0 && (
          <Card className="bg-game-card border-game-muted">
            <CardContent className="pt-6">
              <div className="text-center">
                <Trophy className="h-12 w-12 text-game-muted mx-auto mb-4" />
                <h3 className="text-xl font-bold text-white mb-2">Nenhum torneio encontrado</h3>
                <p className="text-game-text">Tente ajustar o nome da busca.</p>
              </div>
            </CardContent>
          </Card>
        )}

        {tournaments.length > 0 && (
          <div className="grid gap-6">
            {tournaments.map((tournament: Tournament) => (
              <Card key={tournament.tag} className="bg-game-card border-game-muted hover:border-game-orange transition-all">
                <CardContent className="p-6">
                  <div className="flex flex-col lg:flex-row lg:items-start lg:justify-between mb-4">
                    <div className="flex items-center space-x-4 mb-4 lg:mb-0">
                      <div className="w-16 h-16 bg-green-500 bg-opacity-20 rounded-xl flex items-center justify-center">
                        <Trophy className="text-2xl text-green-400" />
                      </div>
                      <div>
                        <h3 className="text-xl font-bold text-white">{tournament.name}</h3>
                        <p className="text-sm text-game-text">Tag: {tournament.tag}</p>
                        <p className="text-game-text text-sm mt-1">{tournament.description}</p>
                        <div className="flex items-center mt-2">
                          <span className={`px-2 py-1 rounded text-xs font-medium ${getStatusColor(tournament.status)}`}>
                            {getStatusText(tournament.status)}
                          </span>
                          <span className="ml-2 px-2 py-1 rounded text-xs font-medium bg-blue-500 bg-opacity-20 text-blue-400">
                            {tournament.type}
                          </span>
                        </div>
                      </div>
                    </div>

                    <div className="grid grid-cols-2 lg:grid-cols-3 gap-4 text-center">
                      <div className="bg-game-muted bg-opacity-30 p-3 rounded-lg">
                        <Users className="h-5 w-5 text-blue-400 mx-auto mb-1" />
                        <div className="text-white font-semibold">{tournament.capacity}/{tournament.maxCapacity}</div>
                        <div className="text-xs text-game-text">Jogadores</div>
                      </div>

                      <div className="bg-game-muted bg-opacity-30 p-3 rounded-lg">
                        <Crown className="h-5 w-5 text-game-yellow mx-auto mb-1" />
                        <div className="text-white font-semibold">{tournament.firstPlaceCardPrize}</div>
                        <div className="text-xs text-game-text">PrÃªmio 1Âº</div>
                      </div>

                      <div className="bg-game-muted bg-opacity-30 p-3 rounded-lg">
                        <div className="text-white font-semibold">Nv. {tournament.levelCap}</div>
                        <div className="text-xs text-game-text">Level Cap</div>
                      </div>
                    </div>
                  </div>

                  <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4 text-sm">
                    <div>
                      <span className="text-game-text">Criado:</span>
                      <div className="text-white">{formatDate(tournament.createdTime)}</div>
                    </div>

                    {tournament.startedTime && (
                      <div>
                        <span className="text-game-text">Iniciado:</span>
